import React from 'react';
import Item from './Item';
import style from './List.module.scss';

function List() {
  let tasks = [{
    task: 'React',
    time: '02:00:00'
  }, {
    task: 'JavaScript',
    time: '01:00:00'
  }, {
    task: 'TypeScript',
    time: '03:00:00'
  }]
  return (
    <aside className={style.listaTarefas}>
      <h2 onClick={() => {
        console.log("h2 clickado: ", tasks)
        tasks = [...tasks, { task: "Status study", time: "05:00:00" }]
      }}>Today's Studies</h2>
      <ul>
        {tasks.map((item, index) => (
          <Item
            key={index}
            {...item}
          />
        ))}
      </ul>
    </aside>
  )
}

export default List;
