import React, { useState } from 'react';
import Item from './Item';
import style from './List.module.scss';

interface ITask {
  task: string,
  time: string
}

function List({ tasks }: { tasks: ITask[]}) {
  return (
    <aside className={style.tasksList}>
      <h2>Today's Studies</h2>
      <ul>
        {tasks.map((item, index) => (
          <Item
            key={index}
            {...item}
          />
        ))}
      </ul>
    </aside>
  )
}

export default List;
